import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ThemeContext, themes } from '../context/themeContext';

function Navbar() {
  const { theme, setTheme } = useContext(ThemeContext);

  const handleThemeChange = (e) => {
    setTheme(themes[e.target.value]);
  };

  // Determine if NEIT theme is selected
  const isNeit = theme === themes.neit;

  return (
    <nav style={{
      backgroundColor: theme.accent,
      color: theme.background,
      padding: '15px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      fontFamily: "'Caveat', cursive",
      fontSize: '24px',
      boxShadow: '0 4px 6px rgba(0,0,0,0.3)',
      borderBottom: `4px dashed ${theme.foreground}`
    }}>
      <Link to="/" style={{ textDecoration: 'none', color: theme.background, display: 'flex', alignItems: 'center' }}>
        {isNeit && (
          <img
            src=""
            alt="NEIT Logo"
            style={{ height: '40px', marginRight: '10px' }}
          />
        )}
        <span style={{ fontWeight: 'bold', fontSize: '30px' }}>LeyLeys</span>
      </Link>
      <div>
        <Link to="/" style={{ color: theme.background, marginRight: '20px', textDecoration: 'none' }}>Home</Link>
        <Link to="/products" style={{ color: theme.background, textDecoration: 'none' }}>Products</Link>
        <select onChange={handleThemeChange} style={{ marginLeft: '20px', fontSize: '16px' }}>
          <option value="light">Light</option>
          <option value="dark">Dark</option>
          <option value="neit">NEIT</option>
        </select>
      </div>
    </nav>
  );
}

export default Navbar;
