import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

function ProductPage() {
  const { id } = useParams();

  const navigate = useNavigate();

  // Local state to store the single product's data
  const [product, setProduct] = useState(null);

  // useEffect runs when the component loads or when the id changes
  useEffect(() => {
    // Fetch the full list of products from the JSON file
    axios.get('/products.json')
      .then((response) => {
        // Find the specific product that matches the current ID
        const found = response.data.products.find(p => p.id === parseInt(id));
        // Save that product to local state
        setProduct(found);
      })
      .catch((error) => {
        // If something goes wrong, log the error
        console.error("Error loading product:", error);
      });
  }, [id]); // Re-run the effect if the `id` changes

  // If the product hasn't been loaded yet, show a loading message
  if (!product) return <p>Loading product...</p>;

  // Display the product information once it's available
  return (
    <div style={{
      padding: '20px',
      color: '#FFD700', // Funky yellow text for the caution-theme
      fontFamily: "'Caveat', cursive" // Whimsical font
    }}>
      {/* Product title */}
      <h1>{product.title}</h1>

      {/* Product image */}
      <img src={product.image} alt={product.title} width="300" />

      {/* Product description */}
      <p>{product.description}</p>

      {/* Product price */}
      <p><strong>Price:</strong> ${product.price}</p>

      {/* Button to go back to the product list page */}
      <button onClick={() => navigate('/products')}>Back</button>
    </div>
  );
}

// Export the component so React Router can render it when visiting /products/:id
export default ProductPage;
