import { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import useFetchProducts from '../hooks/useFetch';
import { ThemeContext } from '../context/themeContext';

function ProductList() {
  const { products, loading } = useFetchProducts('/products.json');
  const [search, setSearch] = useState('');
  const navigate = useNavigate();
  const { theme } = useContext(ThemeContext);

  if (loading) return <p style={{ color: theme.foreground }}>Loading products...</p>;
  if (!products || products.length === 0) return <p style={{ color: theme.foreground }}>No products found.</p>;

  return (
    <div style={{
      backgroundColor: theme.background,
      color: theme.foreground,
      padding: '30px',
      minHeight: '100vh',
    }}>
      <h1>Product List</h1>
      <input
        type="text"
        placeholder="Search products..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{
          padding: '10px',
          fontSize: '16px',
          marginBottom: '20px',
          borderRadius: '6px',
        }}
      />
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {products
          .filter((p) => p.title.toLowerCase().includes(search.toLowerCase()))
          .map((product) => (
            <div
              key={product.id}
              style={{
                backgroundColor: theme.accent,
                color: theme.background,
                borderRadius: '8px',
                padding: '10px',
                margin: '10px',
                width: '200px',
                fontFamily: "'Caveat', cursive",
                cursor: 'pointer',
                transition: 'transform 0.3s ease',
              }}
              onClick={() => navigate(`/products/${product.id}`)}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
            >
              <img src={product.image} alt={product.title} width="100%" />
              <h4>{product.title}</h4>
              <p>${product.price}</p>
            </div>
          ))}
      </div>
    </div>
  );
}

export default ProductList;
